var CookieNotice=new function(){this.config={cookie_name:"cookie-notice-status",cookie_value:"cookie-accepted",notification_message:"Questo sito utilizza cookie di profilazione, propri o di altri, per analizzare il traffico degli utenti e offrire servizi personalizzati. Se accedi a un qualunque elemento sottostante questo banner acconsenti all'uso del cookie.",accept_button:"&times;",notice_id:"cookie-notice-box",policy_url:false,policy_text:false};this.init=function(a){$.extend(this.config,a);if(this.getCookie(this.config.cookie_name)!=this.config.cookie_value){this.draw()}};this.accept=function(){this.setCookie(this.config.cookie_name,this.config.cookie_value,30);$("#"+this.config.notice_id).remove()};this.draw=function(){var a=this.config.notification_message;if(this.config.policy_url!=false&&this.config.policy_text!=false){a+='<a style="color: #e0e0e0; font-weight: bold;" href="'+this.config.policy_url+'" target="_blank">'+this.config.policy_text+"</a>"}var b='<div id="'+this.config.notice_id+'" class="cookie-notice" style="z-index: 9999999; position: fixed; background-color: rgba(0,0,0,0.7); color: white; padding: 10px; top:0; left: 0; right: 0; font-size: 16px;"><div class="cookie-notice-message" style="width:90%; float: left;">'+a+'</div><div class="cookie-notice-buttons" style="float: right; font-size: 30px;"><a href="#" onClick="CookieNotice.accept()" style="cursor: pointer; color: white;">'+this.config.accept_button+"</a></div></div>";$("body").append(b)};this.setCookie=function(b,f,c){var e=new Date();e.setTime(e.getTime()+(c*24*60*60*1000));var a="expires="+e.toUTCString();document.cookie=b+"="+f+"; "+a};this.getCookie=function(d){var b=d+"=";var a=document.cookie.split(";");for(var e=0;e<a.length;e++){var f=a[e];while(f.charAt(0)==" "){f=f.substring(1)}if(f.indexOf(b)==0){return f.substring(b.length,f.length)}}return""}};